mapper.data:
{
  "username": "Pipeline Monitor",
  "avatar_url": "https://i.imgur.com/6VBx3io.png",
  "content": ":x: **Pipeline FAILED**",
  "embeds": [
    {
      "title": "❌ Pipeline Failure Detected",
      "description": "A workflow has failed.",
      "color": 15158332,
      "fields": [
        {
          "name": "Repository",
          "value": "{{1.repository.full_name}}",
          "inline": false
        },
        {
          "name": "Branch",
          "value": "{{1.workflow_run.head_branch}}",
          "inline": true
        },
        {
          "name": "Status",
          "value": "{{1.workflow_run.conclusion}}",
          "inline": true
        }
      ],
      "url": "{{1.workflow_run.html_url}}",
      "timestamp": "{{formatDate(now; \ + "YYYY-MM-DDTHH:mm:ssZ\")}}",
      "footer": {
        "text": "CI/CD Pipeline Monitor • Make.com"
      }
    }
  ]
}
